function d\u00e9finirNomUtilisateur(){var t=prompt("Veuillez saisir votre nom.");localStorage.setItem("nom",t),monTitre.textContent="Mozilla est cool, "+t}var monImage=document.querySelector("html");monImage.onclick=function(){"images/firefox-icon.png"===monImage.getAttribute("src")?monImage.setAttribute("src","images/Paris2.jpg"):monImage.setAttribute("src","images/firefox-icon.png")};var monBouton=document.querySelector("button"),monTitre=document.querySelector("h1");if(localStorage.getItem("nom")){var nomEnregistr\u00e9=localStorage.getItem("nom");monTitre.textContent="Mozilla est cool, "+nomEnregistr\u00e9}else d\u00e9finirNomUtilisateur();monBouton.onclick=function(){d\u00e9finirNomUtilisateur()};